def generate_loot():
loot_table = {
"coin": 0.5, # 50% chance
"key": 0.3, # 30% chance
"heart": 0.15, # 15% chance
"rare_item": 0.00000000000001 # xD% chance
}

loot_items = list(loot_table.keys())
loot_weights = list(loot_table.values())

return random.choices(loot_items, weights=loot_weights, k=1)[0]

# Example Usage
for _ in range(5):
print(f"Loot Drop: {generate_loot()}")
